    private void clearButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_clearButtonActionPerformed
        nameSelect.setSelectedIndex(0);
        classSelect.setSelectedIndex(0);
        typeSelect.setSelectedIndex(0);
        summary.setText("");
        cmd.setText("");
        key1.setText("");
        occur.setText("");
        repro.setText("");
        other.setText("");
    }//GEN-LAST:event_clearButtonActionPerformed

    private void createButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_createButtonActionPerformed
        int i = new File("bugs").listFiles().length;
        String name = "bugs/" +nameSelect.getSelectedItem().toString() + "_" 
                + classSelect.getSelectedItem().toString() + "_" 
                + typeSelect.getSelectedItem().toString() + "_"
                + key1.getText() + "-" + i + ".txt";
        try {
            Date d = new Date();
            try (FileWriter fw = new FileWriter(new File(name))) {
                fw.write(d + "\n");
                fw.write("Tester: "
                        + nameSelect.getSelectedItem().toString());
                fw.write("\nClass: " 
                        + classSelect.getSelectedItem().toString());
                fw.write("\nType: " 
                        + typeSelect.getSelectedItem().toString());
                fw.write("\nSummary: " 
                        + summary.getText() + "\n");
                
                fw.write("\nCommand List:\n");
                for (String cmd1 : cmds) {
                    fw.write("   " + cmd1 + "\n");
                }
                
                fw.write("\nError List:");
                for (String err1 : errs) {
                    fw.write("   " + err1 + "\n");
                }
                
                fw.write("\n\nReproduction Steps:\n");
                fw.write(repro.getText());
                fw.write("\n\nComments:\n");
                fw.write("\n" + other.getText());
            }
        } catch (IOException e) {}
    }//GEN-LAST:event_createButtonActionPerformed

